mapper.update:
update course
set name_school = #{nameSchool,jdbcType=VARCHAR},
  name_teacher = #{nameTeacher,jdbcType=VARCHAR},
  price_course = #{priceCourse,jdbcType=VARCHAR}
where name_course = #{nameCourse,jdbcType=VARCHAR}

mapper.select:
select name_course, name_school, name_teacher, price_course
from course
where name_course = #{nameCourse,jdbcType=VARCHAR}

