mapper.update:
update course
set name_school_a = #{nameSchoolA,jdbcType=VARCHAR},
  name_teacher = #{nameTeacher,jdbcType=VARCHAR},
  price_course = #{priceCourse,jdbcType=VARCHAR},
  test_colum = #{testColum,jdbcType=VARCHAR}
where name_course = #{nameCourse,jdbcType=VARCHAR}

mapper.select:
select name_course, name_school_a, name_teacher, price_course, test_colum
from course
where name_course = #{nameCourse,jdbcType=VARCHAR}
